import React from "react";
import Task from "./Task";

const Tasks = (props) => {
  return (
    <>
      {props.task.map((task) => (
        <Task id={task.id} text={task.text} day={task.day} onDelete={props.delete}/>
      ))}
    </>
  );
};

export default Tasks;
