import React from "react";
import Task from "./Task";
import {FaTimes} from 'react-icons/fa'

const Tasks = (props) => {
  return (
    <>
      {props.task.map((task) => (
        <Task key={task.id} text={task.text} day={task.day} />
      ))}
    </>
  );
};

export default Tasks;
